Array.prototype.suffle = function () {
    var array = this;

    if (array && array.length > 0) {
        let i = array.length - 1;
        for (; i > 0; i--) {
            const j = Math.floor(Math.random() * (i + 1));
            const temp = array[i];
            array[i] = array[j];
            array[j] = temp;
        }
    }

    return array;
}